window.showNotificationPanel=t=>{const e=document.getElementById("notification-panel"),n=document.getElementById("notification-message");e&&n&&(n.textContent=t,e.classList.remove("hidden"))};document.addEventListener("DOMContentLoaded",()=>{const t=document.getElementById("notification-close"),e=document.getElementById("notification-panel");t&&e&&t.addEventListener("click",()=>{e.classList.add("hidden")})});
